import { browser } from '$app/env';
import { writable } from 'svelte/store';

export const pageTitle = writable<string>('Music Thingy');



interface Source {
	src: string;
	type: string;
}
interface Song {
	title: string;
	artist: string
	album: string;
	album_art: string;
	source: {
		type: string;
		sources: Source[];
	}
	current_time: number;
	queue_position: number;
	is_playing: boolean;
	is_sample: boolean;
}


const sample_song = {
	title: 'Wounds',
	artist: 'Papa Khan',
	album: 'Wounds - NCS',
	album_art: 'data:image/jpeg;base64,',
	source: {
		type: 'audio',
		sources: [
			{
				src: '/Papa Khan - Wounds [NCS Release].mp3',
				type: 'audio/mp3',
			}
		]
		
	},
	current_time: 0,
	queue_position: 0,
	is_playing: true,
	is_sample: true,
}

export const currentStatus = writable<Song>(sample_song);

if (browser) {
	currentStatus.subscribe(cs => localStorage.setItem('currentStatus', JSON.stringify(cs)));
}

export const Queue = writable<Array<Song>>(JSON.parse(browser ? localStorage.getItem('Queue') : '[]'));
if (browser) {
	Queue.subscribe(q => localStorage.setItem('Queue', JSON.stringify(q)));
}

